void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 um = iMouse.xy/iResolution.xy;

    vec2 uv = fragCoord/iResolution.xy;
    uv = (uv - 0.5); //-1 to 1

    //uv += -(vec2(sin(iTime), sin(iTime * 2.5)) * 0.1);

    float depth = (uv.x*uv.x + uv.y*uv.y);
    //depth = sin(3.0 * pow(depth, 1.2)) * (depth / 2.0) + depth;

    float angle = atan(uv.x, uv.y) + iTime;

   	vec2 t = vec2(
        angle + 0.2 / depth,
        0.1 / depth + (iTime * 1.2)
    );

    float d = clamp(3.0 * depth, 0.0, 1.0);
    fragColor = texture(iChannel0, t) * vec4(d * (d * 0.5), d, d, d);
}
