void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / 400.0;
    vec3 stone = texture(iChannel1, uv).rgb;
    vec3 wood = texture(iChannel2, uv).rgb;

    float noise0 = texture(iChannel0, uv / 32.0).r;
    float noise1 = texture(iChannel0, uv / 16.0).r;
    float noise2 = texture(iChannel0, uv / 8.0).r;
    float noise3 = texture(iChannel0, uv / 4.0).r;

    float noise = noise0 / 2.0 + noise1 / 4.0 + noise2 / 8.0 + noise3 / 16.0;
    noise = smoothstep(0.3, 0.5, noise);

    vec3 color = noise * stone + (1.0 - noise) * wood;

    // Output to screen
    //fragColor = vec4(vec3(noise),1.0);
    fragColor = vec4(color,1.0);
}
