const int RANGE = 20;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 col = vec3(0);
    for(int i=-RANGE;i<=RANGE;i++) {
        for(int j=-RANGE;j<=RANGE;j++){
            vec2 tuv =  (fragCoord+vec2(i,j) / 3.0) / iResolution.xy;
            vec3 temp = i*i+j*j<RANGE*RANGE ? texture(iChannel1,tuv).xyz : vec3(0,0,0);
            col = max(col, temp);
        }
    }

    // Output to screen
    fragColor = vec4(col,1.0);
}
