float MAX = 20.0;

vec2 po(vec2 z) {
    return vec2(z.x * z.x - z.y * z.y, 2.0 * z.x * z.y);
}

vec4 mende( vec2 pos ) {
    MAX = 13.0 * iTime;

    vec2 z = pos;

    float i = 0.0;

    for(i = 0.0; i < MAX; i++) {
        z = vec2(z.x * z.x - z.y * z.y, 2.0 * z.x * z.y);
        z += pos;
        if (length(z) > 2.0) break;
    }

    float t = cos(iTime);
    i /= MAX;
    return vec4(i, log(length(z)), i / length(z), 1.0);

}

vec2 trata (vec2 idiota) {
    idiota /= iResolution.xy;
    idiota =  idiota * 2.0 - 1.0;
    idiota.x *= iResolution.x / iResolution.y;
    return idiota;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = trata(fragCoord);
	//vec2 mouse = trata(iMouse.xy);

    //vec2 zoom = vec2(-0.749755,0.1006);
    vec2 zoom = vec2(-0.745816,0.1050918);

    uv /= exp(iTime-1.0);
    //uv += trata(iMouse.xy * uv.x);
    uv += zoom;

    // Output to screen
    fragColor = mende(uv);
}
