void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    uv -= iMouse.xy/iResolution.xy;
    uv /= 3.0;

    float a = atan(uv.y, uv.x);
    float m = sqrt( dot(uv, uv) );

    uv.x = cos(a) * (m + 0.05);
    uv.y = sin(a) * (m + 0.05);

    uv += iMouse.xy/iResolution.xy;

    fragColor = texture(iChannel0, uv);
}
