    float circ = 9.+sin(iTime*0.7)*3.;
    vec2 cen = iResolution.xy/2.;
    if (distance(cen,fragCoord) < circ && distance(cen,fragCoord) > circ-1.) {
    //if (distance(vec2(0.5),uv) < edge+onep && distance(vec2(0.5),uv) > edge) {
    	col = vec3(1.+sin(iTime*8.))*0.25;
    }
    vec2 buv = uv;
    buv -= 0.5;
