#define UINT_MAX 0xFFFFFFFF

float mymod(const float a, const float b)
{
    float oneOverB = 1.0 / b;
    return  a - b * floor(a * oneOverB);
}


float wang_hash(vec2 co)
{
	uint seed = floatBitsToUint(dot(co.xy ,vec2(12.9898,78.233)));
	seed = (seed ^ uint(61)) ^ (seed >> uint(16));
	seed *= uint(9);
	seed = seed ^ (seed >> 4);
	seed *= uint(0x27d4eb2d);
	seed = seed ^ (seed >> 15);
    float f = float(seed);
	return (cos(f) + 1.0) / 2.0;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    highp float rand = wang_hash(fragCoord);
    vec3 col = vec3(rand,rand,rand);

    if(rand > 1.0)
    {
    	col = vec3(1.0,0.0,0.0);
    }



    // Output to screen
    fragColor = vec4(col,1.0);
}
