vec3 desat (vec3 c) {
    return vec3(0.299*c.r + 0.587*c.g + 0.114*c.b);
}

vec4 desat (vec4 c) {
    vec4 d = vec4(0.299*c.r + 0.587*c.g + 0.114*c.b);
    d.a = 1.0;
    return d;
}

float luminance(vec3 color)
{
    return dot(color, vec3(1.0/3.0));
}

vec3 convertToYCrCb(vec3 rgbColor)
{
	float Y = 0.2989 * rgbColor.r + 0.5866 * rgbColor.g + 0.1145 * rgbColor.b;
	float Cr = 0.7132 * (rgbColor.r - Y);
	float Cb = 0.5647 * (rgbColor.b - Y);

	return vec3(Y, Cr, Cb);
}

// Checks how far 'away' two colors are.
float getChromaDistance(vec3 YCbCrA, vec3 YCbCrB)
{
	vec2 CbCrA = vec2(YCbCrA.y, YCbCrA.z);
	vec2 CbCrB = vec2(YCbCrB.y, YCbCrB.z);

	return distance(CbCrA, CbCrB);
}

const vec3 TargetColor = vec3(0.618, 0.33, 0.23);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec3 uv_color = texture(iChannel0, uv).rgb;
    vec3 uv_color_y = convertToYCrCb(uv_color);

    vec3 grayscale = vec3 (1.0, 0.0, 0.0);
   	//vec3 grayscale = desat(uv_color);
    // vec3 grayscale = vec3(luminance(uv_color));
    vec3 grayscale_y = convertToYCrCb(grayscale);

    vec3 d = vec3(getChromaDistance(uv_color_y, grayscale_y));

    // Output to screen
    fragColor = vec4(d, 1.0);
}
