vec3 hsv2rgb(vec3 _input)
{
    float      hh, p, q, t, ff;
    int        i;
    vec3       _out;

    if(_input.y <= 0.0) {       // < is bogus, just shuts up warnings
        _out.rgb = vec3(_input.z);
        return _out;
    }
    hh = _input.x;
    if(hh >= 360.0) hh = 0.0;
    hh /= 60.0;
    i = int(hh);
    ff = hh - float(i);
    p = _input.z * (1.0 - _input.y);
    q = _input.z * (1.0 - (_input.y * ff));
    t = _input.z * (1.0 - (_input.y * (1.0 - ff)));

    switch(i) {
    case 0:
        _out.r = _input.z;
        _out.g = t;
        _out.b = p;
        break;
    case 1:
        _out.r = q;
        _out.g = _input.z;
        _out.b = p;
        break;
    case 2:
        _out.r = p;
        _out.g = _input.z;
        _out.b = t;
        break;

    case 3:
        _out.r = p;
        _out.g = q;
        _out.b = _input.z;
        break;
    case 4:
        _out.r = t;
        _out.g = p;
        _out.b = _input.z;
        break;
    case 5:
    default:
        _out.r = _input.z;
        _out.g = p;
        _out.b = q;
        break;
    }
    return _out;     
}

const float PI = 3.1315;

float rad2deg(float a) {
    return a/PI*180.;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord)/iResolution.xy;    
    float anim = mod(iTime*0.1f,1.);
    float h = anim;
    vec3 col = hsv2rgb(vec3(rad2deg(h*2.*PI),uv.x,uv.y));   
    fragColor = vec4(col,1.0);
}
