// PICO-8 16 COLOR PALETTE
const vec3 col0 = vec3(000./255., 000./255., 000./255.);
const vec3 col1 = vec3(029./255., 043./255., 083./255.);
const vec3 col2 = vec3(126./255., 037./255., 083./255.);
const vec3 col3 = vec3(000./255., 135./255., 081./255.);
const vec3 col4 = vec3(171./255., 082./255., 054./255.);
const vec3 col5 = vec3(095./255., 087./255., 079./255.);
const vec3 col6 = vec3(194./255., 195./255., 199./255.);
const vec3 col7 = vec3(255./255., 241./255., 232./255.);
const vec3 col8 = vec3(255./255., 000./255., 077./255.);
const vec3 col9 = vec3(255./255., 163./255., 000./255.);
const vec3 colA = vec3(255./255., 236./255., 039./255.);
const vec3 colB = vec3(000./255., 228./255., 054./255.);
const vec3 colC = vec3(041./255., 173./255., 255./255.);
const vec3 colD = vec3(131./255., 118./255., 156./255.);
const vec3 colE = vec3(255./255., 119./255., 168./255.);
const vec3 colF = vec3(255./255., 204./255., 170./255.);

void mainImage(out vec4 fragColor, in vec2 fragCoord)
	{
    vec2 uv = fragCoord/iResolution.xy;
    vec4 tex = texture(iChannel0,uv);
    vec3 col = tex.rgb;

    float gamma = 1.5;
    col.r = pow(abs(col.r),gamma);
    col.g = pow(abs(col.g),gamma);
    col.b = pow(abs(col.b),gamma);

    float dist0 = length(col - col0);
    float dist1 = length(col - col1);
    float dist2 = length(col - col2);
    float dist3 = length(col - col3);
    float dist4 = length(col - col4);
    float dist5 = length(col - col5);
    float dist6 = length(col - col6);
    float dist7 = length(col - col7);
    float dist8 = length(col - col8);
    float dist9 = length(col - col9);
    float distA = length(col - colA);
    float distB = length(col - colB);
    float distC = length(col - colC);
    float distD = length(col - colD);
    float distE = length(col - colE);
    float distF = length(col - colF);

    float d = min(min(dist0,dist1),min(dist2,dist3));
          d = min(min(dist4,dist5),d);
          d = min(min(dist6,dist7),d);
          d = min(min(dist8,dist9),d);
          d = min(min(distA,distB),d);
          d = min(min(distC,distD),d);
          d = min(min(distE,distF),d);

    if(d == dist0)
        col = col0;
    else if(d == dist1)
        col = col1;
    else if(d == dist2)
        col = col2;
    else if(d == dist3)
        col = col3;
    else if(d == dist4)
        col = col4;
    else if(d == dist5)
        col = col5;
    else if(d == dist6)
        col = col6;
    else if(d == dist7)
        col = col7;
    else if(d == dist8)
        col = col8;
    else if(d == dist9)
        col = col9;
    else if(d == distA)
        col = colA;
    else if(d == distB)
        col = colB;
    else if(d == distC)
        col = colC;
    else if(d == distD)
        col = colD;
    else if(d == distE)
        col = colE;
    else
        col = colF;

    if (fragCoord.x > iMouse.x)
    	fragColor = vec4(col.rgb,tex.a);
    else
        fragColor = tex;
	}
