#define INTENSITY 1.6
#define GLOW 1.2

vec3 blob(vec2 uv, vec3 color, vec2 speed, vec2 size, float time) {
    vec2 point = vec2(
        sin(speed.x * time) * size.x,
        cos(speed.y * time) * size.y
    );

    float d = distance(uv, point);
    d = pow(d / INTENSITY, GLOW);

    return vec3(color.r * d, color.g * d, color.b * d);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = -1.0 + 2.0 * (fragCoord.xy / iResolution.xy);

    float time = iTime * 0.2;

    float freqBlue = texture(iChannel0, vec2(0.01,0.25)).x * 4.0;
    float freqGreen = texture(iChannel0, vec2(0.1,0.25)).x * 2.0;

    //Colours for the blobs
    //0B0F32 = 11, 15, 50 = 0.043 0.059 0.196
    //111744 = 17, 23, 68 = 0.067 0.090 0.267
    vec4 color = vec4(0.043, 0.059, 0.196, 1.0);
    color.rgb += blob(uv,
                      vec3(0.024, 0.031, 0.071),
                      vec2(2.5, 2.2),
                      vec2(0.9, 0.4),
                      time);
    color.rgb += blob(uv,
                      vec3(0.024, 0.031, 0.071),
                      vec2(1.2, 2.3),
                      vec2(0.4, 0.3),
                      time);
    color.rgb += blob(uv,
                      vec3(-0.024, -0.031, -0.071),
                      vec2(2.3, 2.1),
                      vec2(0.4, 0.6),
                      time);
    color.rgb += blob(uv,
                      vec3(-0.024, -0.031, -0.071),
                      vec2(2.1, 1.0),
                      vec2(0.2, 0.8),
                      time);
    fragColor = color;
}
