#define COMPLEXITY 16
#define SPEED 0.4
#define SCALE 1.0

#define MIRRORED_REPEAT(uv) abs(2.*fract(uv/2.)-1.)

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{      
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 m = ( iMouse.xy / iResolution.xy ) + vec2(0.5,-1.);
    vec2 p = uv * SCALE;
	for ( int i = 1; i < COMPLEXITY; i++ ) {
        float f = float( i * 10 ) * m.x + 1.;
		p += ( iTime * 0.001 );
		p.x += m.y / f * sin( ( f * p.y ) + ( iTime * SPEED ) + ( 0.3 * f ) );
		p.y += m.y / f * sin( ( f * p.x ) + ( iTime * SPEED ) + ( 0.3 * f + 30. ) );
	}
    fragColor = texture(iChannel0, MIRRORED_REPEAT(p));
}

#define SRGB_TO_LINEAR(c) pow(c, vec3(2.2))
#define LINEAR_TO_SRGB(c) pow(c, vec3(1.0 / 2.2))

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{      
	vec2 uv = fragCoord.xy / iResolution.xy;

	vec3 tl = SRGB_TO_LINEAR(vec3(1., 0., 0.));
	vec3 tr = SRGB_TO_LINEAR(vec3(1., 1., 0.));
	vec3 bl = SRGB_TO_LINEAR(vec3(0., 1., 1.));
	vec3 br = SRGB_TO_LINEAR(vec3(0., 1., 0.));

    vec3 gradient = LINEAR_TO_SRGB( mix( mix( tr, tl, uv.x ), mix( br, bl, uv.x ), uv.y ) );

    fragColor = vec4( gradient, 1. );
}
