void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv; // = ((120.0 + iTime) / 3.0) * ((fragCoord.xy - (iResolution.xy / 2.0)) / iResolution.y);

    uv = vec2(0, 0.5 * 3.14159) + (vec2(0, iResolution.y / 2.0) - fragCoord.xy) / (iResolution.x / 3.14159 / 2.0);//) * iResolution.y / 2000.0;

    float r = 0.0 / 4.0 ; //mod(iTime / 100.0 + acos(-1.0) / 4.0, acos(-1.0)*2.0);
    mat2 rot = mat2(cos(r), -sin(r), sin(r), cos(r));
    uv = uv * rot;

    float value = sin(uv.x) * sin(uv.y) * iTime * 6.0;
    float color = sin(value) * 3.0;
    //color = color * sign(uv.x) * sign(uv.y); // revision 2

    float low = abs(color);
    float med = abs(color) - 1.0;
    float high = abs(color) - 2.0;
    if(color > 0.0) {
	  fragColor = vec4(med, high, high,1.0);
    } else {
      fragColor = vec4(high, high, med,1.0);
    }
}
