//
// You can use this shader as a template for ray marching shaders
// based from tuto shader :  https://www.shadertoy.com/view/WtGXDD

#define MAX_STEPS 100
#define MAX_DIST 10.
#define SURF_DIST .001

#define S smoothstep
#define T iTime

mat2 Rot(float a) {
    float s=sin(a), c=cos(a);
    return mat2(c, -s, s, c);
}

float Hash21(vec2 p) {
    p = fract(p*vec2(123.34,233.53));
    p += dot(p, p+23.234);
    return fract(p.x*p.y);
}

float sdBox(vec3 p, vec3 s) {
    p = abs(p)-s;
	return length(max(p, 0.))+min(max(p.x, max(p.y, p.z)), 0.);
}

float sdForm(vec3 p, float scale, float scale2, float thickness, float bias) {
	p *= scale;
    return length(cross(sin(p), cos(p.zxy))-bias)/scale2-thickness;
}

vec3 Transform(vec3 p) {
    p.xy *= Rot(T * p.z*.0025);
	p.z -= T*.1;
    p.y -= 0.15*sin(T*.22);
    p.x -= 0.12*cos(T*.33);
    return p;
}

float GetDist(vec3 p) {

    p = Transform(p);

    float f1 = sdForm(p, 40.5 ,58., .028, 0.2); 
    float d = sdBox(p, vec3(1));

    return f1; // max(d, f1);
}

float RayMarch(vec3 ro, vec3 rd) {
	float dO=0.;

    for(int i=0; i<MAX_STEPS; i++) {
    	vec3 p = ro + rd*dO;
        float dS = GetDist(p);
        dO += dS;
        if(dO>MAX_DIST || abs(dS)<SURF_DIST) break;
    }

    return dO;
}

vec3 GetNormal(vec3 p) {
	float d = GetDist(p);
    vec2 e = vec2(.001, 0);

    vec3 n = d - vec3(
        GetDist(p-e.xyy),
        GetDist(p-e.yxy),
        GetDist(p-e.yyx));

    return normalize(n);
}

vec3 GetRayDir(vec2 uv, vec3 p, vec3 l, float z) {
    vec3 f = normalize(l-p),
        r = normalize(cross(vec3(0,1,0), f)),
        u = cross(f,r),
        c = f*z,
        i = c + uv.x*r + uv.y*u,
        d = normalize(i);
    return d;
}

float simple_triangle(float x, float freq){
	x*=freq;
	return -(abs(mod(x,1.)-.5)-.25)*4.;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
	vec2 m = iMouse.xy/iResolution.xy;


    // Normalized pixel coordinates (from 0 to 1)
    vec2 uvn = fragCoord/iResolution.xy;

    // the sound texture is 512x2
    int tx = int(uvn.x*512.0);

	// first row is frequency data (48Khz/4 in 512 texels, meaning 23 Hz per texel)
	float fft  = texelFetch( iChannel0, ivec2(tx,0), 0 ).x; 

    // second row is the sound wave, one texel is one mono sample
    float wave = texelFetch( iChannel0, ivec2(tx,1), 0 ).x;

    float F = 0.08 * simple_triangle(T, 0.1);
    uv += sin(uv*20.+T) * F;
    uv += 0.025*wave;

    vec3 col = vec3(0);

    vec3 ro = vec3(0, 0, -3);
    //ro.yz *= Rot(-m.y*3.14+1.);
    //ro.xz *= Rot(-m.x*6.2831);

    vec3 lookat = vec3(0,0,0);
    vec3 rd = GetRayDir(uv, ro, lookat, 1.);

    float d = RayMarch(ro, rd);

    if(d<MAX_DIST) {
    	vec3 p = ro + rd * d;
    	vec3 n = GetNormal(p);
        p = Transform(p);
    	float dif = dot(n, normalize(vec3(1,2,3)))*.5+.5;

    	col += dif*dif;  

        float f1 = sdForm(p, 50.5,25., .18, 0.1);
        col += f1*vec3(1,.4,.1);
    }

    col = pow(col, vec3(.4545));	// gamma correction

    fragColor = vec4(col,1.0);
}
