vec2 random2( vec2 p ) {
    return fract(sin(vec2(dot(p,vec2(127.1,311.7)),dot(p,vec2(269.5,183.3))))*43758.5453);
}
// 2D Random
float random (in vec2 st) {
    return fract(sin(dot(st.xy,vec2(12.9898,78.233)))*43758.5453123);
}

float vornoi(vec2 p, float t) {
    vec2 i_st = floor(p);
    vec2 f_st = fract(p);
    float m_dist = 1.;
    for (int y= -1; y <= 1; y++) {
        for (int x= -1; x <= 1; x++) {
            vec2 neighbor = vec2(float(x),float(y));
            vec2 point = random2(i_st + neighbor);
            point = 0.5 + 0.5*sin(t + 6.2831*point);
            vec2 diff = neighbor + point - f_st;
            float dist = length(diff);
            m_dist = min(m_dist, dist);
        }
    }
    return m_dist;
}


// 2D Noise based on Morgan McGuire @morgan3d
// https://www.shadertoy.com/view/4dS3Wd
float noise (in vec2 st) {
    vec2 i = floor(st);
    vec2 f = fract(st);
    float a = random(i);
    float b = random(i + vec2(1.0, 0.0));
    float c = random(i + vec2(0.0, 1.0));
    float d = random(i + vec2(1.0, 1.0));
    // Cubic Hermine Curve.  Same as SmoothStep()
    vec2 u = f*f*(3.0-2.0*f);
    return mix(a, b, u.x) +
            (c - a)* u.y * (1.0 - u.x) +
            (d - b) * u.x * u.y;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    vec2 st = fragCoord.xy/iResolution.xy;
    st.x *= iResolution.x/iResolution.y;

    // snow particles using a clamped vornoi texture
    vec2 wind = vec2(iTime * 1.0,0.0) * 1.5;
    float w = 0.;
    w += (1.-min(1.0,smoothstep(0., 0.08,vornoi(st*2.+wind*1.8,iTime))+0.1))*0.8;
    w += (1.-min(1.0,smoothstep(0., 0.02,vornoi(st*2.+wind*0.7,iTime+10.0))+0.1))*1.0;
    w += (1.-min(1.0,smoothstep(0., 0.03,vornoi(st*2.+wind*0.6,iTime+20.0))+0.1))*1.1;
    w = max(0.0,min(1.0,w*5.)) * 0.8;

    // background color
    vec2 h = vec2(
        noise(st*0.4+vec2(iTime / -8., iTime / 5.)),
        noise(st*0.4+vec2(100.+iTime / -8., 100.+iTime / 5.))
    );
    vec3 b = vec3(h*0.8,1.0);

    // noise layer
    b *= 0.4 + noise((st + vec2(iTime/-7.,iTime/8.)) * 250.) * 0.03;

    fragColor = vec4(mix(vec3(1.0,1.0,1.0),b,1.0-w*0.6),1.0);
}
