void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float fadeAfter = 1.0;
    float rgDrain = 1.25;
    float bDrain = 1.4;
    float totalTime = 2.2;

    vec2 uv = fragCoord/iResolution.xy;

	vec4 textureColor = texture( iChannel0, uv );

    float timer = mod( iTime, totalTime );

    if( timer >= fadeAfter )
    {
    	textureColor.rg = mix(textureColor.rg, vec2(0.0),(timer - fadeAfter) / (rgDrain - fadeAfter));
        textureColor.b = mix(textureColor.b, 0.0, (timer - fadeAfter) / (bDrain - fadeAfter) );
    }

    fragColor = textureColor;
}
