void mainImage( out vec4 O, vec2 U )
{
    U/=iResolution.xy;
    O = texture(iChannel0,U);
    if (U.x<.5) O = pow(O,vec4(1./2.2));                 // to flat space

    float l = U.y>.5 ? (O.r+O.g+O.b)/3.                  // isoluminance
                     : dot(O.rgb,vec3(.299,.587,.114));  // luma

    O = .5 + 20.*(O-l);                                  // amplifies difference with B&W

    if (U.x<.5) O = pow(O,vec4(2.2));                    // to sRGB
}
