// simple plane textured floor with fract etc ..
// Gigatron FR
//
float Plan(vec3 pos, vec3 dir)
{
    return pos.y / dir.y;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy *2.-1. ;

    vec2 uv2 = fragCoord/iResolution.xy;

    vec3 tx2 = texture(iChannel1,uv2).xyz ;

	vec3 pos = vec3(0.0, 2.0+sin(iTime), 1.0-iTime*2.5);
    vec3 dir = normalize(vec3(uv, 1.0));


    float d =  Plan(pos, dir );
    pos += d * dir;

    vec3 col = vec3(dir)+sin(iTime*2.0)*0.5+0.5;
    uv  =  vec2(fract(pos.x),fract(pos.z)) ;


    if(uv2.y>0.5)   fragColor  = vec4(tx2*col, 1.0);  

     vec3 tx = texture(iChannel0,1.-uv).xyz ;


    if(uv2.y<0.48)   {

        fragColor  = vec4(tx, 1.0);  
    // cut edge ; 
   		 float bord = 0.96;

		if ( uv.x < - bord || uv.x > bord || uv.y < - bord || uv.y > bord ) {

		    	fragColor = vec4( vec3(dir+sin(iTime)*0.5+0.5),1.0 ) *3.0;

		}

                     }

}
