#define C(a) sin(6.283/8.*dot(U,sin(a+vec2(0,11)))) // trame with angle a

void mainImage( out vec4 O, vec2 U )
{
    O = textureLod(iChannel0,U/iResolution.xy,2.);

    O = pow( mix(vec4(1), vec4( C(0.), C(2.1), C(-2.1), 1),.6) ,
          // vec4(4.*clamp(1.-(2.*O.r-O.g),0.,1.)) );
                  4.*clamp(1.-1.2*O,0.,1.) );
}
