// Fork of "Last Fluid" by wyatt. https://shadertoy.com/view/3tcSDj
// 2020-02-28 04:10:38

float ln (vec3 p, vec3 a, vec3 b) {return length(p-a-(b-a)*dot(p-a,b-a)/dot(b-a,b-a));}
Main {
    vec4 b = B(U);
    Neighborhood;
    vec3 no = normalize(vec3(grd,.01)),
        re = reflect(no,vec3(0,0,1));
    Q = atan(max(vec4(b.x,0.3*abs(b.x),-b.x,1),0.)*5.);//0.5+0.5*sin(min(10.*b.x,1.)/3.+vec4(1,2,3,4));

    Q *= 0.25+exp(-.01*ln(0.5*R.xyy,vec3(U,b.x),vec3(U,b.x)+re));
}

// Fluid step 1 : d/dt V = d/dx P

Main {

    float f = A(U).x;
	Q = B(U-B(U).xy);
    Neighborhood;
    Q.xy -= grd/(-f*0.5+2.);  
    Neighborhood3;
    Q.xy += sign(U.x-0.5*R.x)*f*(grd);
    Q.xy *= 0.999;
   Border Q.xy *= 0.;
}

// Fluid step 2 : d/dt P = d/dx V

Main {
	Q = B(U-A(U).xy);
    Neighborhood;
    Q.w  -= div;
    Neighborhood2;
    Q.xyz -= 0.25*(N*n.y-S*s.y+E*e.x-W*w.x).xyz;
    if (iFrame < 1) Q.x = -1.;
    Input
        Q.x = 1.;
    Border Q.x = -1.;
}

// gaussian blur pass 1
Main {
	Q = vec4(0);
	loop Q += gau(i) * B(U+vec2(i,0)).x;

}

// gaussian blur pas2
Main {
	Q = 0.1*D(U);
	loop Q += gau(i) * C(U+vec2(0,i));
    Q.w = 0.1*(Q.x);
}
