////////////////////////////////////////////////////////////////////////////////
//
// Snub hexagonal tiling plus dual.
// cf. https://www.shadertoy.com/view/3tyXWw by fizzer
//
// <mouse>: translate, or move Wythoff point
// d: show dual
// f: show fundamental region
// m: change mouse mode
// r: show basic grid regions
// t: show tiling
// z: auto zoom
//
// Moving the Wythoff point makes the tiling non-uniform and in some
// positions, the tiling not drawn completely.
//
////////////////////////////////////////////////////////////////////////////////

const float X = 1.732050808; // sqrt(3)
const float Y = 0.577350269; // 1/sqrt(3)

const mat2 M = 0.5*mat2(1,-X,1,X);
const mat2 Minv = Y*mat2(X,X,-1,1);

vec2 reflection(vec2 p, vec2 q, vec2 r) {
  // reflect p in the line through q and r
  p -= q;
  vec2 n = (r-q).yx * vec2(1,-1);
  p -= 2.0*dot(p,n)*n/dot(n,n);
  p += q;
  return p;
}

float segment(vec2 p, vec2 a, vec2 b) {
  vec2 pa = p - a;
  vec2 ba = b - a;
  float h = clamp(dot(pa, ba) / dot(ba, ba), 0.0, 1.0);
  float d = length(pa - ba * h);
  return d;
}

float line(vec2 p, vec2 a, vec2 b) {
  vec2 pa = p-a;
  vec2 ba = b-a;
  float h = dot(pa,ba)/dot(ba,ba);
  return length(pa - ba * h);
}

const int CHAR_D = 68;
const int CHAR_F = 70;
const int CHAR_G = 70;
const int CHAR_M = 77;
const int CHAR_R = 82;
const int CHAR_T = 84;
const int CHAR_U = 85;
const int CHAR_Z = 90; // zooming

bool key(int key) {
    return texelFetch(iChannel0, ivec2(key,2),0).x != 0.0;
}

vec3 diagram(vec2 z, float scale) {
  vec2 z0 = z;
  z.x += 0.5;
  z = Minv*z; // Convert to square grid
  ivec2 index = ivec2(floor(z)); // Remember cell in grid
  z -= floor(z);
  z = M*z; // Back to triangles

  // Map to equilateral triangles [(0,0),(1,0),(0.5,0.866)],[(0,0),(1,0),(0.5,-0.866)]
  z.x -= 0.5;
  bool inlower = z.y < 0.0; // Point is in lower triangle,
  if (inlower) z = -z;      // so rotate to upper triangle

  // All patterns have rotational symmetry about triangle centre, so:
  // map centre of triangle to origin...
  z.y -= 0.5*Y;

  // ...and reflect in planes of symmetry of triangle
  const vec2 A = 0.5*(vec2(1,X));  // normalized
  const vec2 B = 0.5*(vec2(-1,X)); // normalized
  float ta = dot(z,A);
  z -= 2.0*max(ta,0.0)*A;
  float tb = dot(z,B);
  z -= 2.0*max(tb,0.0)*B;
  z.y += 0.5*Y;        // Shift origin back to region centre,
  if ((ta > 0.0) != (tb > 0.0)) z.x = -z.x; // Want even number of reflections

  vec3 col = vec3(1);
  vec3 col3 = vec3(1,0,0);
  vec3 col4 = vec3(1,1,0);

  bool inregion = length(z-z0) < 1e-4;

  scale *= 2.0*X;
  z *= 2.0*X; // Scale so region is 1 by X

  float pwidth = scale*3.0/iResolution.y;
  float eps = 0.01;
  float lwidth0 = 2.0*eps;
  float lwidth1 = max(lwidth0+pwidth,4.0*eps);
  float bwidth = max(pwidth,1.0*eps);

  if (!key(CHAR_R)) {
    float d = min(abs(z.x),z.y);
    d = min(d,line(abs(z),vec2(0,1),vec2(X,0)));
    float mfact = smoothstep(-bwidth,bwidth,d);
    col = mix(col3,col4,(z.x < 0.0 ? mfact : mfact-1.0));
    if (key(CHAR_F) && !inregion) col = mix(col,0.8*col,mfact);
  }

  vec2 p = vec2(0.48,0.44); // The snub Wythoff point
  if (!key(CHAR_M) && iMouse.x > 0.0) {
    p = iMouse.xy/iResolution.y;
  }

  // Now reflect p in various lines of symmetry to get the
  // actually points of the tilings.
  // This really needs a picture.
  vec2 p0 = vec2(p.x,-p.y);
  vec2 p1 = vec2(-p.x,p.y);
  vec2 p2 = reflection(p,vec2(X,0),vec2(0,1));
  vec2 p3 = -p2;
  vec2 p4 = reflection(-p,vec2(-X,0),vec2(0,1));
  vec2 p5 = reflection(p,vec2(-X,0),vec2(0,1));
  vec2 p6 = reflection(p,vec2(-X,0),vec2(0,1));
  vec2 p7 = reflection(p1,vec2(-X,0),vec2(0,1));

  // The centre point of various triangles.
  // This needs a picture as well.
  vec2 t1 = (p0+p1+p2)/3.0;
  vec2 t2 = (p0+p1+p3)/3.0;
  vec2 t3 = (p1+p2+p6)/3.0;
  vec2 t4 = -(-t1-vec2(-X,0))+vec2(-X,0);
  vec2 t5 = (p1+p4+p6)/3.0;
  vec2 t6 = -(t5-vec2(-X,0))+vec2(-X,0);

  // Draw the lines. We have to draw all the lines that
  // intersect with the fundamental region - there are
  // quite a few, some of which only have a few pixels
  // in common.
  vec3 col1 = vec3(0.2);
  vec3 col2 = vec3(0.8);

  // Dual edges
  if (!key(CHAR_D)) {
    float d = 1e8;
    d = min(d,segment(z,t1,t2));
    d = min(d,segment(z,t1,vec2(X,0)));
    d = min(d,segment(z,t1,t3));
    d = min(d,segment(z,t1,t3));
    d = min(d,segment(z,-t1,t4));
    d = min(d,segment(z,-t1,t4));
    //d = min(d,segment(z,t5,t6));
    d = min(d,segment(z,p7,t3));
    //d = min(d,segment(z,p7,-p7));
    col = mix(col2,col,smoothstep(lwidth0,lwidth1,d));
  }

  // Main tiling edges
  if (!key(CHAR_T)) {
    float d = 1e8;
    d = min(d,segment(z,p0,p1));
    d = min(d,segment(z,p1,p2));
    d = min(d,segment(z,p2,p0));
    d = min(d,segment(z,p1,p3));
    d = min(d,segment(z,p1,p4));
    d = min(d,segment(z,p1,p5));
    col = mix(col1,col,smoothstep(lwidth0,lwidth1,d));
  }
  return col;
}

void mainImage(out vec4 fragColor, vec2 fragCoord ) {
  vec2 z = (2.0*fragCoord-iResolution.xy+0.123)/iResolution.y;
  float scale = 1.0;
  if (key(CHAR_Z)) scale = exp(2.0*sin(iTime));
  z *= scale;
  if (key(CHAR_M) && iMouse.x > 0.0) {
    z -= (2.0*iMouse.xy-iResolution.xy)/iResolution.y;
  }
  vec3 col = diagram(z,scale);
  fragColor = vec4(col,1);
}
