float Circle(vec2 uv,vec2 p,float r,float blur)
{
    float d = length(uv-p);
    float c = smoothstep(r,r-blur,d);

    return c;
}

float Smiley(vec2 uv,vec2 p,float size)
{
    uv -= p;
    uv /= size;

    float mask = Circle(uv,vec2(0.0),0.4,0.05);
    mask -= Circle(uv,vec2(0.15,0.1),0.07,0.01);
    mask -= Circle(uv,vec2(-0.15,0.1),0.07,0.01);

    float mouth = Circle(uv,vec2(0.0),0.3,.02);
    mouth -= Circle(uv,vec2(0.0,0.1),0.3,0.02);
    mouth = clamp(mouth, 0.0, 1.0 );

    mask-= mouth;

    return mask;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    uv -= .5;
    uv.x *= iResolution.x/iResolution.y;

    float t = iTime;
    vec2 p = vec2(sin(t)*.7, cos(t)*0.3);

    float mask = Smiley(uv,p,0.5);

    vec3 col = vec3(1.0,1.0,0.0) * mask;

    fragColor = vec4(col,1.0);
}
