float angle(vec2 u, vec2 v) {
  return acos(dot(u, v) / (length(u) * length(v)));
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  vec2 p = fragCoord.xy / iResolution.xy;
  float color = 0.0;
  vec2 c = vec2(0.5);
  float i = float(int(iFrame) % 1000) / 200.0;
  float border = 0.5 * sin(i * 1000.0 * angle(fract(p) - c, vec2(1.0, 0.0)));
  if (length(fract(p) - c) < border) {
    color = 1.0;
  }
  fragColor = vec4(vec3(color), 1.0);
}
