void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = vec4(0.0);
    vec3 col;
    float t;

    for(int c=0;c<3;c++){

        vec2 uv = (fragCoord*10.0-iResolution.xy)/iResolution.y/10.0;
        uv += vec2(iTime/2.0,iTime/3.0)/4.0;
        t = iTime+float(c)/10.;
        float scale = 5.1;
        float scale1 = 1.5;
        for(int i=0;i<15;i++)
        {
            uv = fract(uv/scale1/scale)*scale1*scale;
            uv += fract(uv/25.0);
            uv=fract(uv/(2.1+abs((uv.x-uv.y)/8.0))-(uv.yx/(2.0+(fract(uv.x+uv.y))))/scale)*scale/1.5+scale1*scale;
            uv /= scale1;
        	uv=uv.yx+col.xy;
        }
     col[c] = ((uv.x)-(uv.y));
	}

    fragColor = vec4(vec3(col),1.0);

}
