float circle(vec2 uv, vec2 pos, float radius, float brightness)
{
    float d = length(uv-pos);
    return brightness * smoothstep(d,d+0.01,radius);
}

float circle2(vec2 uv, vec2 pos, float radius, float halo, float brightness)
{
  return brightness * clamp (halo * (radius - length(uv-pos)), 0.0, 1.0);
}


float hexagon(vec2 uv, float size, float brightness) {
    uv = abs(uv);
    float c = dot(uv,normalize(vec2(1, 1.73)));
    c = max(c, uv.x);

    return brightness * step(c, size);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float time = 5.0 + 1.0 * mod(iTime, 5.);
    float t = .50 + 0.5*cos(0.2 * time);
    float v = 0.8 - 0.5*sin(0.2 * time);

    vec2 center = vec2(t, v);

    vec2 uv = (fragCoord -  center * iResolution.xy)/iResolution.y;
    vec2 UV = (fragCoord -  0.5 * iResolution.xy)/iResolution.y;
    vec2 origUV = fragCoord / iResolution.xy;

    vec3 col = texture( iChannel0, origUV).rgb;

    col.r += circle2(UV - 0.4, vec2(0., 0.1),  0.6 + 0.03 * sin(.5 * iTime), 12., 0.8);
    col.g += circle2(UV - 0.7, vec2(0., -0.4), 0.4 + 0.02 * cos(0.2 + .5 * iTime), 12., 0.8);
    col.b += circle2(UV - 0.8, vec2(0., -0.4), 0.3 + 0.02 * sin(1.5 + .5 * iTime), 12., 0.8);

    float items = 
                hexagon(uv, 0.1, 0.2) +
        		circle(uv - 0.17, vec2(0., 0.), 0.02, 0.1) +
        		mix(circle2(uv - 0.217, vec2(0., 0.), 0.04, 12., 0.8),
        		hexagon(uv - 0.25, 0.05, 0.3), 0.25);
    col += items;

    fragColor = vec4(col,1.0);
}
