void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = fragCoord.xy / iResolution.xy; //0 to 1
    uv -= 0.5; //-0.5 to 0.5
    uv.x *= iResolution.x / iResolution.y; //multiply x axis by the aspect ratio of the screen
    float d = length(uv);

    float r = 0.3;
    float c = smoothstep(r, r - 0.02, d);

    fragColor = vec4(vec3(c), 1.0);
}
