void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec4 deform = vec4(.8);
    float DEFORM_SIZE = deform.z;
    float MAX_DISTORTION = deform.w;

    vec2 sample_shift;

    vec2 dist = 0.5 - uv;

    float distortion = -sqrt(0.25 - pow(uv.y * deform.x - deform.x * 0.5, 2.0)) * DEFORM_SIZE + DEFORM_SIZE * 0.5; 

    sample_shift.x = distortion * dist.x * 2.0;

    float deform_y_fixed = (MAX_DISTORTION - distortion) * deform.y;
    sample_shift.y = deform_y_fixed * dist.y;

    vec4 text = texture(iChannel0, uv + sample_shift);


    // Output to screen
    fragColor = text;
}
