#define GR vec3(11,163,37)/255.

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec2 ruv = (fragCoord - iResolution.xy*.5)/iResolution.y;     

    vec3 vcol = texture(iChannel0, uv).rgb;    

    vec3 vcs = abs(vcol - GR);
    float vcm = (vcs.r+vcs.g+vcs.b);
    float vw = clamp(vcm, 0., 1.);

    vec3 col = vw < .4 ? vec3(1.) : vcol;        

    fragColor = vec4(col,1.0);    
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    vec3 col = texture(iChannel0, uv).rgb;    

    int result = 0;
    for(int y = -2; y<=2; y+=1){
        for(int x = -2; x<=2; x+=1){

            vec2 c = fragCoord + vec2(x, y);            
            vec2 ruv = c/iResolution.xy;

            vec3 pcol = texture(iChannel0, ruv).rgb;
            if(pcol.r == 1.){
            	result += 1;
            }

        }    
    }

    if(result >0 && result <16){
    	col = vec3(0.);
    }

    if(result >= 16){
        col = texture(iChannel1, uv).rgb;
    }       

    fragColor = vec4(col,1.0);    
}
