#define lerp(a,b,t)        ( ((a - b) * t) + a )
#define inside(p,rect)     ( step( abs(p-rect.xy-rect.zw/2.), rect.zw/2.) == vec2(1) )
#define move(p,rect,offset) inside(p-offset, rect) ? -offset : vec2(0)

float sqrwv(float off, float t)
{
    float r = mod(t - off, 1.),
          d = mod(t - off, 4.);  
    return d < 1. ? r : d < 2. ? 1. : d < 3. ? 1. - r: 0.;
}

vec2 dance(vec2 uv)
{
    float d = mod(iTime, 4.);

    float t = sin(iTime) * sin(iTime * 0.4);

    float sqr1 = sqrwv(0.,t) * 0.5;
    float sqr2 = sqrwv(1.,t) * 0.5;
    float sqr3 = sqrwv(2.,t) * 0.5;
    float sqr4 = sqrwv(3.,t) * 0.5;


    vec4 rect1 = vec4(0.,0., .5,.5);    
    vec4 rect2 = vec4(0.5,0., .5,.5);
    vec4 rect3 = vec4(0.5,0.5, .5,.5);
    vec4 rect4 = vec4(0.,0.5, .5,.5);


    vec2 fuv;

    fuv = move(uv, rect1, 	vec2(sqr1, 	sqr2));

    if (fuv == vec2(0,0))
    	fuv += move(uv, rect2, 	vec2(-sqr2, sqr1));
    if (fuv == vec2(0,0))
    	fuv += move(uv, rect3, 	vec2(-sqr1, -sqr2));
    if (fuv == vec2(0,0))
        fuv += move(uv, rect4, 	vec2(sqr2, 	-sqr1));
    if (fuv == vec2(0,0))
        return fuv;

    return fuv + uv;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{   
    vec2 uv = dance(fragCoord/iResolution.xy);


   vec3 col = vec3(0,0,0);


    if (uv.x != 0. && uv.y != 0.)
    	col = texture(iChannel0, uv).xyz;
    fragColor = vec4(col,1.0);
}
