const float PI = 3.14159265359;

float mesa(float min, float max, float feather, float x) {
    float r_2 = 0.5 * (max-min);
    float mid = 0.5 * (max+min);
    return 1.0 - smoothstep(0.0, feather, abs(x - mid) - r_2);
}

vec3 hsv2rgb(vec3 c) {
    vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
    return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}

float expImpulse( float k, float x )
{
    float h = k*x;
    return h*exp(1.0-h);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // coordinates from -0.5 to 0.5
    vec2 uv = (fragCoord - 0.5*iResolution.xy) / iResolution.y;
    float ant_count = 9.0 + 1.0*sin(1./(24.0*2.*PI) * iTime);

    // circle stuff
    float r = length(uv);
    float disc = length(uv); 

    float phi = atan(uv.y, uv.x);

    float pos1 = mod(phi + iTime + PI/8.0, 2.0*PI/ant_count);
    float pos2 = mod(phi + iTime, 2.0*PI/ant_count);

    float antness = 1.0; // mesa(0.125, 0.875, 0.025, pos);

    // make some rings
    antness *= 
          mesa(0.125, 0.18, 0., r) * mesa(0.125, 0.875, 0.025, pos2)
        + mesa(0.225, 0.28, 0.01, r) * mesa(0.125, 0.875, 0.025, pos1)
        + mesa(0.325, 0.39, 0.01, r) * mesa(0.125, 0.875, 0.025, pos2)
        + mesa(0.425, 0.45, 0.01, r) * mesa(0.125, 0.875, 0.025, pos1);

    float phiness = 0.5 + phi/(2.0*PI); // phi mapped to [0,1]
    float freq = 0.37 + phiness * 0.5;
    float fft = texture(iChannel0, vec2(freq, 0.25)).x;
    float val = 8.0*fft*(1.0-fft)*fft;
    float hue = (5.0 * iTime + 360.0 * phiness) / 360.0;

    float bri = texture(iChannel0, vec2(uv.x, 0.75)).x;
    vec3 fg_col = hsv2rgb(vec3(hue, bri*0.99, expImpulse(4.0, val)));    
    vec3 bg_col = hsv2rgb(vec3(0.5, bri*0.60, 1.0));
    // bg_col = vec3(0.0);
    vec3 col = mix(
        bg_col, // bg color
        fg_col, // ant color
        antness
    );

    // Output to screen
    fragColor = vec4(col,1.0);
}
