void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = (2.0*fragCoord.xy-iResolution.xy)/iResolution.y*.6;

    float dp = dot(uv*fract(iTime/100.),uv);
	 uv /= dp;
     uv.x=sin(uv.x*fract(iTime/50.)-1.5);
    uv *= .5;
    float a = atan(uv.y,uv.x);
	float t = iTime*.3;
    float r2 = max( .0,  abs(sin(t*.5))*6. - length(uv) );
    t += r2 * r2 *cos(r2*fract(iTime/100.)+t )+a *1.;        
    uv *= mat2( cos(t*fract(iTime/100.)), sin(t), cos(t), cos(t) );
    vec3 col = .1 + cos(uv.y *(cos(t)*2.+0.5) +t) *sin(iTime+uv.yxy*fract(iTime/100.));    
    fragColor = vec4(col,1.0);
}
