//if its black, restart the simulation once buffer C's channel 1 has loaded it's texture

//i use the same calculations for velocity/pressure as in my other shader https://www.shadertoy.com/view/wtjyzc
//except in this one i am using the velocity matrix to simulate the movement of fluids

void mainImage( out vec4 c, in vec2 f )
{ 
    c = texelFetch( iChannel2, ivec2(32,0),0).x>0.?T1(f):T0(f);
}

vec4 S0(vec2 U){return T0(U);}

void F(vec2 p, vec2 offset, vec4 state, inout vec2 v, inout float pr, inout float d, inout float n)
{
    vec4 target = S0(p+offset-V(p+offset).xy);
    offset=normalize(offset);
    v+=offset*(target.w-state.w);
    pr+=target.w;
    d+=dot(offset,target.xy);
    n++;
}

void mainImage(out vec4 state, vec2 p)
{
    state = S0(p-V(p).xy);
    vec2 v = vec2(0);
    float d = 0., pr=0.,n=0.;

    F(p, vec2(0,1), state, v, pr, d, n);
    F(p, vec2(0,-1), state, v, pr, d, n);
    F(p, vec2(1,0), state, v, pr, d, n);
    F(p, vec2(-1,0), state, v, pr, d, n);

    d/=n;
    pr/=n;
    v/=n;

    state.w = pr-d;
	state.xy -= v;   
    state.z = d;
}

vec4 S0(vec2 U){return T0(U);}

void F(vec2 p, vec2 offset, vec4 state, inout vec2 v, inout float pr, inout float d, inout float n)
{
    vec4 target = S0(p+offset-V(p+offset).xy);
    offset=normalize(offset);
    v+=offset*(target.w-state.w);
    pr+=target.w;
    d+=dot(offset,target.xy);
    n++;
}

void mainImage(out vec4 state, vec2 p)
{
    state = S0(p-V(p).xy);
    vec2 v = vec2(0);
    float d = 0., pr=0.,n=0.;

    F(p, vec2(0,1), state, v, pr, d, n);
    F(p, vec2(0,-1), state, v, pr, d, n);
    F(p, vec2(1,0), state, v, pr, d, n);
    F(p, vec2(-1,0), state, v, pr, d, n);

    d/=n;
    pr/=n;
    v/=n;

    state.w = pr-d;
	state.xy -= v;    
    state.z = d;
}

vec4 S0(vec2 U){return T0(U);}

void F(vec2 p, vec2 offset, vec4 state, inout vec2 v, inout float pr, inout float d, inout float n)
{
    vec4 target = S0(p+offset-V(p+offset).xy);
    offset=normalize(offset);
    v+=offset*(target.w-state.w);
    pr+=target.w;
    d+=dot(offset,target.xy);
    n++;
}

void mainImage(out vec4 state, vec2 p)
{
    state = S0(p-V(p).xy);
    vec2 v = vec2(0);
    float d = 0., pr=0.,n=0.;

    F(p, vec2(1,1), state, v, pr, d, n);
    F(p, vec2(-1,-1), state, v, pr, d, n);
    F(p, vec2(1,-1), state, v, pr, d, n);
    F(p, vec2(-1,1), state, v, pr, d, n);


    d/=n;
    pr/=n;
    v/=n;

    state.w = pr-d;
	state.xy -= v;    

    state.z = d;

    if(iMouse.z>1. && length(p-iMouse.xy)-50.<1.)state.xy=T1(p).xy*.1;

	if(iFrame<30)state.xy=T1(p).xy*.1;

    if(p.x<1. || p.y<1. || R.x-p.x<1. || R.y-p.y<1.) state.xy*=0.8;

}

vec4 S0(vec2 U){return T0(U);}
vec4 S1(vec2 U){return T1(U);}

void mainImage(out vec4 state, vec2 f)
{
    for(int i=0;i<5;i++)
    f -= S1(f).xy;

    state = S0(f);

    if(iMouse.z>1. && length(f-iMouse.xy)-50.<1.)state=T2(f);

    if(iFrame<30)state=T2(f);
}
