// 136 chars - Subtracted 2 more chars by using the o.xy=iResolution.xy trick.
void mainImage(out vec4 o, vec2 u){
    u = abs(u - .5*(o.xy = iResolution.xy))/o.y;
    o = texture(iChannel0, (u.x>u.y ? u : u.yx) + sin( iTime*vec2(.02,.03) )  );
}

