#define r (iResolution.xy)
#define t iTime

#define M(q)q*=mat2(sin(t*.1-t/s+vec4(0,11,33,0)))

void mainImage( out vec4 o, in vec2 FC )
{
    o=vec4(0);
	float s,g,e,i,j,m;
    for(;i++<20.;) {
        vec3 p=g*vec3((FC.xy*2.-r)/r.y,1);
        p.z--;
        e=s=2.;
        for(int i;i++<12;s+=s)M(p.xy),M(p.yz),p=abs(p)-.3,e=min(e,(length(p)+1.)/s),p+=p;g+=e;o.xyz+=p/s/e;
    }
    o.xyz*=log(8./e)*.005;
}
