float mirrored(float v) {
    float m = mod(v, 2.0);
    return mix(m, 2.0 - m, step(1.0, m));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float ar = iResolution.x / iResolution.y;
    vec2 uv = fragCoord/iResolution.xy * 2.0 - vec2(1.0);
	uv.y /= ar;

    float r = mirrored(length(uv)*2.0);
    float a = atan(uv.y, uv.x);

    float cr = sin(r*10.0+iTime) * 0.5 + 0.5;
    float ca = sin(a*3.0+iTime) * 0.5 + 0.5;
    float c = cr*ca;
    vec3 col = vec3(c);
    vec3 col2 = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));
    col = mix(col, col2, 0.5);
    fragColor = vec4(col,1.0);
}
