#define A (0.6)
#define F (0.7)
#define S (1.0)

float offset(float y) {
	return A * sin(F*y + S*iTime);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = fragCoord/iResolution.xy;
    vec2 offsets = vec2(0.0, 0.0);
    if (mod(fragCoord.y, 4.0) < 2.0)
    	offsets.x = offset(uv.x);
    else
    	offsets.x = 1.0 - offset(uv.x);

    fragColor = vec4(texture(iChannel0, uv+offsets).rgb, 1.0);
}
