float def=1.;
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.y;
    uv-=vec2(0.5);
    vec2 p=uv;

    //Transforms space
    uv.x=2.*p.x*p.y-iTime*0.01;
    uv.y=p.x*p.x-p.y*p.y+1.-iTime*0.01;
    p=uv;
    uv.x=2.*p.x*p.y-iTime*0.01;
    uv.y=p.x*p.x-p.y*p.y+1.-iTime*0.01;
    p=uv;
    uv.x=2.*p.x*p.y-iTime*0.01;
    uv.y=p.x*p.x-p.y*p.y+1.-iTime*0.01;
    def=iTime*0.1;

    //checkerboard pattern
    float col = smoothstep(-0.01,0.01,cos(uv.x*def)*cos(uv.y*def));

    // Output to screen
    fragColor = vec4(vec3(col),1.0);
}
