#define WHITE vec3(1)
#define PURPLE vec3(220, 190, 251)/255.
#define PURPLE2 vec3(220, 189, 255)/255.
#define PINK vec3(255, 204, 253)/255.
#define S smoothstep

#define SCALE 182.
#define CX 325.
#define CY 182.
#define R0 (526.-CX)/SCALE
#define R1 (537.-CX)/SCALE
#define R2 (567.-CX)/SCALE
#define R3 (591.-CX)/SCALE
#define R4 (750.-CX)/SCALE

#define PI 3.1415926
#define TAU 2.*PI

vec3 color = WHITE;
void addColor(float amount, vec3 c) { color = mix(color, c, amount); }

float triangleRepeat(float f) {return 1.-abs(1.-mod(f,2.));}


float sdLine( in vec2 p, in vec2 a, in vec2 b )
{
    vec2 pa = p-a, ba = b-a;
    float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
    return length( pa - ba*h );
}

vec2 rot(vec2 i, float a) {
    vec2 cs = vec2(cos(a), sin(a));
    return i*mat2(cs.x, -cs.y, cs.y, cs.x);
}

float[] rotateMask = float[] (
    1.
    // 0:00-0:20
    // 0:20-0:40
    // 0:40-1:00
);



// 	0:28 rotation starts
// +37
// 	1:05 bars enter
// +18
// 	1:23 bars exit, rotation stops
// +28
// 	1:51 rotation starts
// +37
// 	2:28 bars enter
// +20
// 	2:48 fade to white

// Bars:
// 65-83, 148-
// :18

// Rot:
// 28-83, 111-
// :55

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord*2. - iResolution.xy)/iResolution.y;
    uv /= min(iTime*2., 1.);

    float A = 1./iResolution.y; // aliasing size?
	float R = length(uv);
    float T = atan(uv.y, uv.x)/PI;
    float t;
    float time = iTime;


    t = T + time*.16;
    float inner_pink_stripe = S(.45, .55, triangleRepeat(t*64.));
	addColor(S(R0-A, R0+A, R) * inner_pink_stripe, PINK);


    t = T - time*.08;
    float heart_t = (fract(t*12.-.5)-.5)/12.*PI;
    vec2 heart_uv = vec2(cos(heart_t), sin(heart_t))*R;
    vec2 heart_line_center = vec2(1.23, 0);
    vec2 heart_line_delta = vec2(0.03, .03);
    float r = .03;
	heart_uv.y = abs(heart_uv.y);
    float heart_mask = S(r+A,r-A,sdLine(heart_uv, 
                                              heart_line_center - heart_line_delta,
                                              heart_line_center + heart_line_delta));
	addColor(S(R1-A, R1+A, R), PURPLE);
	addColor(S(R1-A, R1+A, R)*heart_mask, WHITE);

    t = T + time * .04 ;
    float mask = abs(fract(t*16. - R*3. -.3)-.5);
    mask = S(.25+A,.25-A,abs(mask));
    addColor(S(R2-A, R2+A, R), PINK);
    addColor(S(R2-A, R2+A, R)*mask, WHITE);


    t = T - time*.08;
    mask = abs(fract(t*24. + cos(R*2.)+.5)-.5);
    mask = S(.2+A*4.,.2-A*4.,abs(mask));
    addColor(S(R3-A, R3+A, R), PURPLE2);
    addColor(S(R3-A, R3+A, R)*mask, WHITE);

    //addColor(S(.5-A, .5+A, abs(uv.y)), PINK);


    addColor(S(R4-A, R4+A, R), WHITE);
    // Output to screen
    fragColor = vec4(color,1.0);


}
