void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Translate pixel coordinates to specific window in Mandelbrot
    vec2 uv = (fragCoord-0.5*iResolution.xy)/iResolution.y*.009; //zoom
    uv.x += - 1.11; //x offset
    uv.y += -.26;   //y-offset

    int n = 0;
    vec2 w;

    //Main calcuation loop
    for (int i = 0; i<5000; i=i+1) {
        w = vec2((w.x*w.x)-(w.y*w.y)+uv.x,2.*w.x*w.y + uv.y);
        if (length(w) > 2.) {
            n = i;
            break;
        }
    }


    vec3 col = vec3(float(n)/100.,sin(float(n)/30.+iTime*0.5)*.5+.5,.0); //cycle colors of pixels not belonging to set
    if (n == 0) col *= 0.; //set pixels belonging to set to zero (black)

    //output color
    fragColor = vec4(col,1.0);
}
