const float PI = 3.1415926;
const float WIDTH = 0.03;
const float SPEED = 0.5;
const float RES = 2.0;
const float AMP = 0.8;
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    float x = uv.x;
    x = fract(x+iTime*SPEED);

    vec3 c = vec3(0.0);
    float f = sin(x * PI * 2.0 * RES)*AMP;
    //f = f*x;
    f = f*0.5 + 0.5;
    float d = abs(uv.y - f);
    if( d < WIDTH )
        c = vec3(1.0, 1.0, 0.0)*smoothstep(WIDTH*0.25, WIDTH, WIDTH- d);

    fragColor = vec4(vec3(c),1.0);
}

vec4 someFunction( vec4 a, float b )
{
    return a+b;
}
