void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 xy = fragCoord.xy; //получение корды текущего пикселя
    vec4 solidRed = vec4(0,0.0,0.0,1.0);//пиксель становится чёрным
    if(xy.x > 300.0){//произвольное число
        solidRed.r = 1.0;//присваиваем красному компоненту значение 1.0
    }
    fragColor = solidRed;
}
