// continuation of https://www.shadertoy.com/view/tly3Dz
float circle(in vec2 p)
{
	return length(p) + 0.2;
}

float sdLine( in vec2 p, in vec2 a, in vec2 b )
{
    vec2 pa = p-a, ba = b-a;
    float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
    return length( pa - ba*h );
}

float rand(vec2 n) 
{ 
	return fract(sin(dot(n, vec2(12.9898, 4.1414))) * 43758.5453);
}

float noise(vec2 p)
{
	vec2 ip = floor(p);
	vec2 u = fract(p);
	u = u*u*(3.0-2.0*u);

	float res = mix(
		mix(rand(ip),rand(ip+vec2(1.0,0.0)),u.x),
		mix(rand(ip+vec2(0.0,1.0)),rand(ip+vec2(1.0,1.0)),u.x),u.y);
	return res*res;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 res = iResolution.xy;
    vec2 uv = fragCoord/iResolution.xy;
	vec2 p = ( 2. * fragCoord - res ) / res.y; 

    float c = circle(p);
    float d = smoothstep(0.98, 0.99, c);
    vec3 col = vec3(d-0.1);

    // fake diffuse
    col = mix(vec3(smoothstep(c, 0.98, 0.95)*0.25), col, p.x * (1.0-d));

    // striature
	vec2 pl = p;
    pl.y*=50.0;
    pl.y += noise(p*4.2)*10.0;
    float l = 1.0-sdLine(pl, vec2(-0.9, 0.0), vec2(1.1, -0.25));
    //l = smoothstep(l, 0.2, 0.01);

    // grain
    p*=10.52;
    float g = noise(0.5*iTime+p*160.01);
    g*=1.12; g+= noise(iTime+p*80.02)*0.02;

    col = mix(col, vec3(g*0.1), l*(1.0-c)*(1.0-d));
    col = mix(col, vec3(0.02), l + rand(uv*14.0));
    //col = vec3(l);

    // Output to screen
    fragColor = vec4(col,1.0);
}
