void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = fragCoord/iResolution.xy;

    vec3 col = texture(iChannel0, uv).rgb;

    fragColor = vec4(col,1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = fragCoord / iResolution.xy;

    vec3 video = texture(iChannel0, uv).rgb;
    vec2 theOff;

    float maxd = 0.0;
    for(int i=0; i<6; i++) {
      float a = float(i)*6.28318/6.0;
      vec2 off = 0.03 * vec2(cos(a), sin(a));
      vec3 old = texture(iChannel1, uv + off).rgb;

      float d = distance(old, video);
      if(d > maxd) {
          maxd = d;
          theOff = off;
      }
    }

    vec3 feedback = texture(iChannel1, uv + theOff * 0.05).rgb;

    feedback *= (0.995 + 0.006 * smoothstep(-0.2, 0.2, feedback.r-feedback.g));

    fragColor = vec4(mix(video, feedback, 0.995), 1.0);
}
