const float ShutterSpeed 		= 1000.0/60.0;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.yx;

    // Time varying pixel color
    vec3 col = 0.3 + 0.8*cos(iTime * ShutterSpeed  +uv.xyx+vec3(0.5,0.1,0.2));

    // Output to screen
    fragColor = vec4(col,1.0);
}
