void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    float horizontalMaskThird= step(fragCoord.y,iResolution.y/3.);
    float horizontalMaskTwoThird= step(fragCoord.y,iResolution.y/3.*2.);

    // Time varying pixel color
    vec3 yellow = vec3(252., 209., 22.)/255.;
    vec3 blue = vec3(0., 56., 147.)/255.;
    vec3 red= vec3(206., 17., 38.)/255.;

    vec3 colombiaFlag = mix(blue, red,horizontalMaskThird);

    colombiaFlag = mix(yellow,colombiaFlag,horizontalMaskTwoThird);

    // Output to screen

    vec3 col=colombiaFlag;
    fragColor = vec4(colombiaFlag,1.0);
}
