#define SMOOTHING_SIZE 1.0 / iResolution.y

float noise(in float x) {
    return fract(cos(x * 4754.673) * 573.123);
}

void ImageSprite(inout vec3 color, in sampler2D channel, in vec2 p, in vec2 pos, in vec2 size, in float ori, in float border) {
    float c = cos(ori), s = sin(ori);
    p = (p - pos) * mat2(c, -s, s, c) / size + 0.5;

    float halfBorder = 0.5 * border;
    vec2 corner = abs(p - 0.5) - 0.5;
    float bbox = max(corner.x, corner.y);
    if (bbox < halfBorder) {
        color = texture(channel, p).rgb;
        float smoothing = SMOOTHING_SIZE / size.x;
        color *= smoothstep(-smoothing, smoothing, abs(bbox) - halfBorder);
    }
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;
    vec3 color = texture(iChannel0, fragCoord / iResolution.xy).rgb * 0.8;

    float screenRight = 0.5 * iResolution.x / iResolution.y;
    for (float column=-screenRight; column < screenRight; column += 0.2) {
        vec2 columnUV = uv - vec2(column, 0.0);
        float colId = floor(column / 0.2);

        columnUV.y += noise(colId) * iTime * 4.0 + 1.0;
        float rowId = floor(columnUV.y / 0.4);

        columnUV.x += 0.2 * sin(floor(columnUV.y / 0.4) + iTime);
        columnUV.y = mod(columnUV.y, 0.4) - 0.2;

        float n = noise(colId + 573.674 + rowId * 654.986);
        ImageSprite(color, iChannel0, columnUV, vec2(0.0), vec2(0.25), (n + 3.0) * iTime, 0.025);
    }

    fragColor = vec4(color, 1.0);
}
