void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    const int layers = 5;
    float layer_range = 1.0 / float(layers);

    //vec4 color = vec4(0.0, 0.0, 0.1, 0.2);
    float waves_max_y = 0.85;

    int this_layer = 0;

    vec4 base_color = vec4(0.988, 1.0, 0.859, 1.0);
    vec4 light_water_color = vec4(0.749, 0.922, 1.0, 1.0);
    vec4 dark_water_color = vec4(0.059, 0.369, 0.498, 1.0);
    vec4 color = light_water_color;
    vec4 color_inc = (light_water_color - dark_water_color) / vec4(layers);

    for (int layer = 0; layer < 7; layer++) {
    	float offset = float(layer) / float(layers);
        float fft_sample = offset + (layer_range * uv.x);
        float fft  = texture( iChannel0, vec2(fft_sample,0.25) ).x;
        float max_y = (offset + (fft * layer_range)) * waves_max_y;
        if (uv.y < max_y) {
        	break;
        }

        color = color - color_inc;
        this_layer++;
    }

    if (this_layer >= layers) {
        color = base_color;
    }

    fragColor = color;
}
