void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec2 offset = vec2(0.05);

    // Time varying pixel color
    vec4 col = texture(iChannel0, uv);
    col.r = texture(iChannel0, uv * (vec2(1.0) - offset) + offset).r;

    float radius = 0.5;
	float distanceFromCenter = length(uv - vec2(0.5));
	float circle = clamp((distanceFromCenter - radius)*iResolution.y, 0., 1.);

    col = mix(texture(iChannel0, uv), col, circle);
    // Output to screen
    fragColor = vec4(col.rgb, 1.0);
}
