#define D(v,r) smoothstep( s, -s, length(v) - r )  // draw AA disk

void mainImage( out vec4 O, vec2 u )
{
    vec2  R = iResolution.xy;
    float s = 10./R.y;
    vec2  U = s* u,
          I = ceil(U),                             // cell id
          D = s*cos(10.*iTime+vec2(0,11) + 6.3*fract(1e4*sin(dot(I,R)))), // random rotation
          F = fract(U+D)*2.-1.;                    // local coordinates in cell

    O = vec4(   D(F,s*I.x)                         // disk of size = row
              * min( 1., pow(1.6,I.y) /(I.x*I.x) ) // intensity = 1/area * col
            );

    O = pow(O, vec4(1./2.2) );                     // to sRGB
}
