void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord.xy - .5 * iResolution.xy) / iResolution.y;

  	float l = length(uv);
  	uv += cos(iTime * .12) * .6; //  * .1

    vec3 color = vec3(0.);
  	color += cos(dot(uv, vec2(l, l)) * 40. + iTime * .8) * .5;
  	color *= sin(length(vec2(uv.y, uv.x)) * 20. + iTime * .5) * .3;

  	color += 1. - l * 1.5;
  	color += vec3(l, uv.y, uv.x) * .5;

  	fragColor = vec4(color, 1.);
}
